<?php

namespace App\Services\Tts;

use App\Models\TtsVoice;

class MockTtsProvider implements TtsProviderInterface
{
  private const SILENT_MP3_BASE64 = '';

  public function synthesize(string $text, TtsVoice $voice): TtsSynthesisResult
  {
    $content = base64_decode(self::SILENT_MP3_BASE64, true) ?: '';

    return new TtsSynthesisResult(
      $content,
      'audio/mpeg',
      'mp3',
      [
        'provider' => 'mock',
        'voice_code' => $voice->code,
        'text_length' => mb_strlen($text),
      ],
    );
  }
}
